fn value(code: char) -> i32 {
    if code == '(' {
        1
    } else {
        -1
    }
}

fn lift(input: &str) -> i32 {
    input
        .chars()
        .map(|c| value(c))
        .reduce(|a, b| a + b)
        .unwrap()
}

fn lift2(input: &str) -> usize {
    let mut sum = 0;
    for (position, code) in input.chars().enumerate() {
        sum += value(code);
        if sum < 0 {
            return position + 1;
        }
    }
    0
}

