#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn lift1_codevalue() {
        assert_eq!(value('('), 1);
        assert_eq!(value(')'), -1);
    }

    #[test]
    fn lift1_0() {
        assert_eq!(lift("(())"), 0);
        assert_eq!(lift("()()"), 0);
    }

    #[test]
    fn lift1_3() {
        assert_eq!(lift("((("), 3);
        assert_eq!(lift("(()(()("), 3);
        assert_eq!(lift("))((((("), 3);
    }

    #[test]
    fn lift1_n1() {
        assert_eq!(lift("())"), -1);
        assert_eq!(lift("))("), -1);
    }

    #[test]
    fn lift1_n3() {
        assert_eq!(lift(")))"), -3);
        assert_eq!(lift(")())())"), -3);
    }

    #[test]
    fn lift2_1() {
        assert_eq!(lift2(")"), 1);
    }

    #[test]
    fn lift2_5() {
        assert_eq!(lift2("()())"), 5);
    }
}
