fn value(code: char) -> i32 {
    if code == '(' {
        1
    } else {
        -1
    }
}

fn lift(input: &str) -> i32 {
    input
        .chars()
        .map(|c| value(c))
        .reduce(|a, b| a + b)
        .unwrap()
}

