#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_codevalue() {
        assert_eq!(value('('), 1);
        assert_eq!(value(')'), -1);
    }

    #[test]
    fn test_0() {
        assert_eq!(lift("(())"), 0);
        assert_eq!(lift("()()"), 0);
    }

    #[test]
    fn test_3() {
        assert_eq!(lift("((("), 3);
        assert_eq!(lift("(()(()("), 3);
        assert_eq!(lift("))((((("), 3);
    }

    #[test]
    fn test_n1() {
        assert_eq!(lift("())"), -1);
        assert_eq!(lift("))("), -1);
    }

    #[test]
    fn test_n3() {
        assert_eq!(lift(")))"), -3);
        assert_eq!(lift(")())())"), -3);
    }
}
